import React, { useEffect, useState } from 'react';
import style from './festival.module.css';

const FestivalShowcase = () => {
  const celebrityEvents = [
    { day: 'Day 1', title: 'Lalbaugcha Raja', year: '2024', image: 'https://www.hindustantimes.com/ht-img/img/2024/09/07/550x309/Lalbaugcha_Raja_1725699025748_1725699026078.jfif' },
    { day: 'Day 2', title: 'Lalbaugcha Raja', year: '2024', image: 'https://blog.cdn.level.game/2023/09/manishaleveldesigner_render_an_image_of_lord_ganesha_with_all_h_bad14d0b-9524-4c98-9ef7-6115454ed4f9.png' },
    { day: 'Day 3', title: 'Lalbaugcha Raja', year: '2024', image: 'https://www.hindustantimes.com/ht-img/img/2024/09/07/550x309/Lalbaugcha_Raja_1725699025748_1725699026078.jfif' },
    { day: 'Day 4', title: 'Lalbaugcha Raja', year: '2024', image: 'https://blog.cdn.level.game/2023/09/manishaleveldesigner_render_an_image_of_lord_ganesha_with_all_h_bad14d0b-9524-4c98-9ef7-6115454ed4f9.png' },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const isMobile = window.innerWidth <= 768;

    if (isMobile) {
      const interval = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % celebrityEvents.length);
      }, 2000);

      return () => clearInterval(interval); // Cleanup on unmount
    }
  }, [celebrityEvents.length]);

  return (
    <div className={style.festivalshowcasecontainer}>
      <h2>Celebrities at Lalbaugcha Raja</h2>
      <div className={`${style.celebritygrid} ${style.slider}`}>
        {celebrityEvents.map((event, index) => (
          <div
            className={style.celebrityitem}
            key={index}
            style={{
              display: currentIndex === index || window.innerWidth > 768 ? 'block' : 'none',
            }}
          >
            <img src={event.image} alt={`${event.day} ${event.title}`} style={{height:"130px"}}/>
            <div className={style.celebrityinfo}>
              <h4>{event.day}</h4>
              <p>{event.title} {event.year}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FestivalShowcase;
