import React, { useState } from 'react';
import style from './addBanner.module.css';

const AdBanner = () => {
  const [showAd, setShowAd] = useState(true);
  const [showOptions, setShowOptions] = useState(false);

  const closeAd = () => {
    setShowOptions(true);
  };

  const handleOptionClick = (option) => {
    if (option === "not-interested") {
      setShowAd(false);
    } else {
      setShowOptions(false); // Keep the ad visible
    }
  };

  return (
    showAd && (
      <div className={style.container}>
        <div className={style.adbanner}>
          <div className={style.adcontent}>
            <img
              src="https://png.pngtree.com/thumb_back/fh260/background/20191113/pngtree-film-film-stage-blue-glitter-movie-background-image_322039.jpg"
              alt="Ad Logo"
              className={style.adlogo}
            />
            <div className={style.adtext}>
              <p className={style.adtitle}>A DT TV Exclusive Film</p>
              <h1>HELLARO</h1>
              <p>Watch Now on IMDb, Rating: 8.2/10</p>
            </div>
            <a href="https://www.imdb.com" target="_blank" rel="noopener noreferrer" className={style.adbtn}>
              Watch Now
            </a>
          </div>

          {!showOptions ? (
            <button className={style.adclosebtn} onClick={closeAd}>
              âœ–
            </button>
          ) : (
            <div className={style.optionMenu}>
              <p onClick={() => handleOptionClick("not-interested")}>Not Interested</p>
              <p onClick={() => handleOptionClick("keep-suggesting")}>Keep Suggesting</p>
            </div>
          )}
        </div>
      </div>
    )
  );
};

export default AdBanner;
