import React, { useState, useEffect } from 'react';
import OwlCarousel from 'react-owl-carousel2';
import 'react-owl-carousel2/lib/styles.css'; // Import carousel styles
import style from './showcase.module.css';

const Showcase = () => {
    const [videos, setVideos] = useState([]);
    const [selectedVideo, setSelectedVideo] = useState(null);

    // Fetch videos from the API
    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/api/movies/');
                const data = await response.json();
                console.log('Fetched videos:', data); // Log API response
                setVideos(data || []); // Store the videos in state
            } catch (error) {
                console.error('Error fetching videos:', error);
            }
        };
    
        fetchVideos();
    }, []);

    const handleVideoClick = (videoUrl) => {
        setSelectedVideo(videoUrl); // Set the video URL to play in modal
    };

    const closeModal = () => {
        setSelectedVideo(null); // Close the modal by resetting video URL
    };

    const options = {
        items: 5,
        nav: true,
        rewind: true,
        autoplay: true,
        autoplayTimeout: 5000,
        loop: true,
        margin: 10,
        responsive: {
            0: {
                items: 1,
            },
            600: {
                items: 3,
            },
            1000: {
                items: 5,
            }
        }
    };

    return (
        <div className={style.container}>
            <div className={style.gallerycontainer}>
                {/* Owl Carousel for video gallery */}
                <OwlCarousel options={options}>
                    {videos.map((video, index) => (
                        <div 
                            key={index} 
                            className={style.galleryitem} 
                            onClick={() => handleVideoClick(video.movie_file)} // Click to open video in modal
                        >
                            {/* Render the poster image */}
                            {video.poster_image && (
                                <img 
                                    src={video.poster_image} 
                                    alt={video.title} 
                                    className={style.posterImage} 
                                />
                            )}
                        </div>
                    ))}
                </OwlCarousel>

                {/* Modal to play selected video */}
                {selectedVideo && (
                    <div className={style.modal} onClick={closeModal}>
                        <span className={style.close}>&times;</span>
                        <video className={style.modalcontent} controls autoPlay>
                            <source src={selectedVideo} type="video/mp4" />
                            Your browser does not support the video tag.
                        </video>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Showcase;
