import React, { useState, useEffect } from 'react';
import style from './showcase.module.css';

const Showcase = () => {
    const [videos, setVideos] = useState([]);
    const [isPlayerOpen, setIsPlayerOpen] = useState(false);
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [currentIndex, setCurrentIndex] = useState(0);

    // Fetch videos from the API
    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/api/movies/');
                const data = await response.json();
                setVideos(data || []); // Set the fetched videos data
            } catch (error) {
                console.error('Error fetching videos:', error);
            }
        };

        fetchVideos();
    }, []);

    // Open video player modal
    const openPlayer = (video) => {
        setSelectedVideo(video);
        setIsPlayerOpen(true);
    };

    // Close video player modal
    const closePlayer = () => {
        setIsPlayerOpen(false);
        setSelectedVideo(null);
    };

    // Handle next slide
    const nextSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % videos.length);
    };

    // Handle previous slide
    const prevSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + videos.length) % videos.length);
    };

    return (
        <div className={style.showcaseContainer}>
            <h2>Movie Showcase</h2>
            <div className={style.slider}>
                <button onClick={prevSlide} className={style.slideButton}>❮</button>
                <div className={style.videoGrid} style={{ transform: `translateX(-${currentIndex * 100}%)` }}>
                    {videos.map((video, index) => (
                        <div
                            key={index}
                            className={style.videoItem}
                            onClick={() => openPlayer(video)} // Open player on click
                        >
                            <img
                                src={video.poster_image}
                                alt={video.title}
                                className={style.posterImage}
                            />
                        </div>
                    ))}
                </div>
                <button onClick={nextSlide} className={style.slideButton}>❯</button>
            </div>

            {/* Modal for playing the video */}
            {isPlayerOpen && (
                <div className={style.playerOverlay} onClick={closePlayer}>
                    <div className={style.playerContainer} onClick={(e) => e.stopPropagation()}>
                        <button onClick={closePlayer} className={style.closeButton}>X</button>
                        {selectedVideo && (
                            <video controls className={style.videoPlayer} autoPlay>
                                <source src={selectedVideo.movie_file} type="video/mp4" />
                                Your browser does not support the video tag.
                            </video>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Showcase;
