import React, { useState } from 'react';

const FileUpload = () => {
  const [title, setTitle] = useState('');
  const [genre, setGenre] = useState('');
  const [language, setLanguage] = useState('');
  const [releaseDate, setReleaseDate] = useState('');
  const [castCrew, setCastCrew] = useState('');
  const [trailerUrl, setTrailerUrl] = useState('');
  const [synopsis, setSynopsis] = useState('');
  const [movieFile, setMovieFile] = useState(null); // Video file state
  const [posterImage, setPosterImage] = useState(null); // Image file state
  const [rating, setRating] = useState('');
  const [status, setStatus] = useState('Published');
  const [message, setMessage] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal open/close state
  const [videoSrc, setVideoSrc] = useState(null); // Video source

  // Function to handle opening the video modal
  const handleVideoPreview = () => {
    if (movieFile) {
      const videoURL = URL.createObjectURL(movieFile);
      setVideoSrc(videoURL);
      setIsModalOpen(true);
    }
  };

  // Function to close the modal
  const handleModalClose = () => {
    setIsModalOpen(false);
    setVideoSrc(null);
  };

  // Form submission handler
  const handleSubmit = async (e) => {
    e.preventDefault();

    const movieData = {
      title,
      genre,
      language,
      release_date: releaseDate,
      cast_crew: castCrew,
      trailer_url: trailerUrl,
      synopsis,
      rating,
      status,
    };

    try {
      const response = await fetch('http://127.0.0.1:8000/api/movies/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(movieData),
      });

      if (response.ok) {
        setMessage('Movie/TV Show uploaded successfully!');
      } else {
        const errorData = await response.json();
        setMessage(`Error: ${errorData.error || 'Failed to upload.'}`);
        console.error('Error details:', errorData);
      }
    } catch (error) {
      setMessage('Error: Unable to connect to the server.');
      console.error('Network error:', error);
    }
  };

  return (
    <div className='p-2'>
      <h3 className='ms-3 my-1'>
        <span>Dashboard</span><span>/</span>Upload a File
      </h3>
      <hr />
      <div>
        <form onSubmit={handleSubmit}>
          <div className='row'>
            {/* Your form fields here */}
            
            <div className="mb-3 col-lg-6">
              <label htmlFor="poster_image" className="form-label">Poster Image</label>
              <input 
                type="file" 
                className="form-control" 
                id="poster_image" 
                onChange={(e) => setPosterImage(e.target.files[0])} 
                required
              />
              {posterImage && (
                <img 
                  src={URL.createObjectURL(posterImage)} 
                  alt="Poster Preview" 
                  className="img-thumbnail mt-2" 
                  style={{ width: '100px', cursor: 'pointer' }} 
                />
              )}
            </div>

            <div className="mb-3 col-lg-6">
              <label htmlFor="movie_file" className="form-label">Movie File</label>
              <input 
                type="file" 
                className="form-control" 
                id="movie_file" 
                onChange={(e) => setMovieFile(e.target.files[0])} 
              />
              {movieFile && (
                <button 
                  type="button" 
                  className="btn btn-primary mt-2" 
                  onClick={handleVideoPreview}
                >
                  Preview Movie
                </button>
              )}
            </div>

            {/* Other form fields and submit button */}
            <div className="col-lg-12">
              <button className='btn btn-success' type="submit">Save</button>
              <button className='btn ms-1' type="button" style={{backgroundColor:'#33526E',color:'#fff'}}>Cancel</button>
            </div>
          </div>

          {/* Display success or error message */}
          {message && <div className="alert alert-info mt-3">{message}</div>}
        </form>
      </div>

      {/* Modal for playing the video */}
      {isModalOpen && (
        <div className="modal" style={{ display: 'block' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Movie/TV Show Preview</h5>
                <button type="button" className="btn-close" onClick={handleModalClose}></button>
              </div>
              <div className="modal-body">
                {videoSrc && (
                  <video controls width="100%">
                    <source src={videoSrc} type="video/mp4" />
                    Your browser does not support the video tag.
                  </video>
                )}
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleModalClose}>Close</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FileUpload;
