import React, { useEffect, useState } from 'react';
import style from './festival.module.css';

const FestivalShowcase = () => {
  const [celebrityEvents, setCelebrityEvents] = useState([]); // State for holding events
  const [currentIndex, setCurrentIndex] = useState(0);

  // Fetching data from the API
  useEffect(() => {
    const fetchCelebrityEvents = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/movies/reerr/');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        // Assuming the API returns an array of events
        setCelebrityEvents(data); // Adjust based on your API's response structure
      } catch (error) {
        console.error('Error fetching celebrity events:', error);
      }
    };

    fetchCelebrityEvents();
  }, []);

  useEffect(() => {
    const isMobile = window.innerWidth <= 768;

    if (isMobile) {
      const interval = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % celebrityEvents.length);
      }, 2000);

      return () => clearInterval(interval); // Cleanup on unmount
    }
  }, [celebrityEvents.length]);

  return (
    <div className={style.festivalshowcasecontainer}>
      <h2>Celebrities at Lalbaugcha Raja</h2>
      <div className={`${style.celebritygrid} ${style.slider}`}>
        {celebrityEvents.map((event, index) => (
          <div
            className={style.celebrityitem}
            key={index}
            style={{
              display: currentIndex === index || window.innerWidth > 768 ? 'block' : 'none',
            }}
          >
            {/* Adjusting the img src and title based on your API response */}
            <img src={event.poster_image} alt={`${event.day} ${event.title}`} style={{ height: '150px' }} />
            <div className={style.celebrityinfo}>
              <h4>{event.day}</h4>
              <p>{event.title} {event.year}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FestivalShowcase;
