import React, { useState, useEffect } from 'react';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import style from './showcase.module.css'; // Assuming you have your styles here

const Showcase = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);

  // Fetch videos from the API
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/movies/');
        const data = await response.json();
        console.log(data); // Check the API response in the console
        setVideos(data || []); // Adjusted to directly set data
      } catch (error) {
        console.error('Error fetching videos:', error);
      }
    };

    fetchVideos();
  }, []);

  const handleVideoClick = (videoUrl) => {
    setSelectedVideo(videoUrl);
  };

  const closeModal = () => {
    setSelectedVideo(null);
  };

  return (
    <div className={style.container}>
      {/* Owl Carousel */}
      <OwlCarousel
        className="owl-theme"
        loop
        items={3} // Show 3 items at once (adjust as needed)
        autoplay
        autoplayTimeout={3000}
        margin={10}
        dots={true}  // Show dots for navigation
        nav={true}   // Enable next/prev navigation
        responsive={{
          0: { items: 1 },    // For mobile
          600: { items: 2 },  // For tablet
          1000: { items: 3 }  // For desktop
        }}
      >
        {videos.map((video, index) => (
          <div key={index} className={style.item}>
            <div className={style.galleryitem} onClick={() => handleVideoClick(video.movie_file)}>
              {/* Render poster image */}
              {video.poster_image && (
                <img
                  src={video.poster_image}
                  alt={video.title}
                  className={style.poster_image}
                />
              )}
            </div>
          </div>
        ))}
      </OwlCarousel>

      {/* Modal to show the selected video */}
      {selectedVideo && (
        <div className={style.modal} onClick={closeModal}>
          <span className={style.close}>&times;</span>
          <video className={style.modalcontent} controls autoPlay>
            <source src={selectedVideo} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      )}
    </div>
  );
};

export default Showcase;
