import React, { useEffect, useState } from 'react';
import MovieCard from './MovieCard';
import style from './renderMovie.module.css';

const App = () => {
  const [movies, setMovies] = useState([]); // State to hold movie data
  const [error, setError] = useState(null); // State to handle any errors
  const [isPlayerOpen, setIsPlayerOpen] = useState(false); // State to control video player visibility
  const [selectedMovie, setSelectedMovie] = useState(null); // State to store selected movie details
  const [isLoading, setIsLoading] = useState(false); // State for loading spinner

  // Fetch movies data from the API
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/movies/?language=Hindi'); // Ensure you update the endpoint for your use case
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();

        // Check if data is an array and set state
        if (Array.isArray(data)) {
          setMovies(data);
        } else {
          console.error('Data is not an array:', data);
          setError('Invalid data format received from the server.');
        }
      } catch (error) {
        console.error('Error fetching movies:', error);
        setError(error.message); // Set the error message to state
      }
    };

    fetchMovies();
  }, []);

  // Open the video player modal with loading and delay
  const openPlayer = (movie) => {
    setSelectedMovie(movie); // Set the clicked movie as the selected movie
    setIsLoading(true); // Show loading indicator

    // Simulate loading (like Shemaroo's movie startup)
    setTimeout(() => {
      setIsLoading(false); // Hide loading after delay
      setIsPlayerOpen(true); // Show the video player modal
    }, 1000); // 1 second delay to simulate loading experience
  };

  // Close the video player modal
  const closePlayer = () => {
    setIsPlayerOpen(false); // Hide the video player modal
    setSelectedMovie(null); // Clear the selected movie
  };

  return (
    <div>
      <h1 className={style.eventstitle} id="hindi">GUJARATI MOVIE</h1>
      {error && <p className={style.error}>{error}</p>} {/* Display error message if there's an error */}
      <div className={style.movielist}>
        {movies.length > 0 ? (
          movies.map((movie) => (
            <div 
              key={movie.id} 
              className={style.movieitem} 
              onClick={() => openPlayer(movie)} // Open player on image click
            >
              <MovieCard
                image={movie.poster_image} // Use the correct property for image URL from your API response
                title={movie.title}
                genre={movie.genre}
                language={movie.language}
              />
            </div>
          ))
        ) : (
          <p>No movies available.</p> // Message when no movies are found
        )}
      </div>

      {/* Modal for video player */}
      {isPlayerOpen && (
        <div className={style.playerOverlay} onClick={closePlayer}>
          <div className={style.playerContainer}>
            <button onClick={closePlayer} className={style.closeButton}>X</button>
            {selectedMovie && (
              <video controls className={style.videoPlayer} autoPlay>
                <source src={selectedMovie.movie_file} type="video/mp4" /> {/* Use movie file instead of trailer */}
                Your browser does not support the video tag.
              </video>
            )}
          </div>
        </div>
      )}

      {/* Loading Spinner */}
      {isLoading && (
        <div className={style.loadingOverlay}>
          <div className={style.loadingSpinner}></div> {/* CSS spinner or loading animation */}
          <p>Loading...</p>
        </div>
      )}
    </div>
  );
};

export default App;
