import React, { useEffect, useState } from 'react';
import Slider from "react-slick";
import ReactPlayer from 'react-player';
import style from './movie.module.css';

const MovieCarousel = () => {
  const [slides, setSlides] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch('https://yourapi.com/movies'); // Replace with your API endpoint
        const data = await response.json();
        setSlides(data);
      } catch (error) {
        console.error('Error fetching movie data:', error);
      }
    };

    fetchMovies();
  }, []);

  // Function to handle click on the "PLAY MOVIE" button
  const handlePlayMovieClick = (url) => {
    setSelectedMovie(url);
    setIsPlaying(true); // Set the playing state to true
  };

  const handleClosePlayer = () => {
    setIsPlaying(false);
    setSelectedMovie(null);
  };

  return (
    <div className={style.container}>
      <div className={style.carouselcontainer}>
        <Slider {...settings}>
          {slides.map(slide => (
            <div key={slide.id} className={style.carouselslide}>
              <div className={style.bannercontent}>
                <img src={slide.imageUrl} alt={slide.title} className={style.bannerimage} />
                <div className={style.moviedetails}>
                  <h2 className={style.movietitle}>{slide.title}</h2>
                  <p className={style.moviesubtitle}>{slide.subtitle}</p>
                  <div className={style.movielanguages}>
                    {slide.languages ? slide.languages.join(' | ') : 'Languages not available'}
                  </div>
                  <button
                    onClick={() => handlePlayMovieClick(slide.fileUrl)} // Call the handler on click
                    className={style.playmoviebutton}
                  >
                    PLAY MOVIE
                  </button>
                </div>
              </div>
            </div>
          ))}
        </Slider>
      </div>

      {isPlaying && selectedMovie && (
        <div className={style.playerOverlay}>
          <div className={style.playerContainer}>
            <button className={style.closeButton} onClick={handleClosePlayer}>
              X
            </button>
            <ReactPlayer 
              url={selectedMovie} 
              playing 
              controls 
              width="100%" 
              height="100%" 
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default MovieCarousel;
