import React, { useEffect, useState } from 'react';
import Slider from "react-slick";
import style from './movie.module.css';

const MovieCarousel = () => {
  const [slides, setSlides] = useState([]);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch('https://yourapi.com/movies'); // Replace with your API endpoint
        const data = await response.json();
        setSlides(data);
      } catch (error) {
        console.error('Error fetching movie data:', error);
      }
    };

    fetchMovies();
  }, []);

  return (
    <div className={style.container}>
      <div className={style.carouselcontainer}>
        <Slider {...settings}>
          {slides.map(slide => (
            <div key={slide.id} className={style.carouselslide}>
              <div className={style.bannercontent}>
                <img src={slide.imageUrl} alt={slide.title} className={style.bannerimage} />
                <div className={style.moviedetails}>
                  <h2 className={style.movietitle}>{slide.title}</h2>
                  <p className={style.moviesubtitle}>{slide.subtitle}</p>
                  <div className={style.movielanguages}>
                    {slide.languages ? slide.languages.join(' | ') : 'Languages not available'}
                  </div>
                  <a href={slide.fileUrl} target="_blank" rel="noopener noreferrer" className={style.watchnowbutton}>
                    WATCH NOW
                  </a>
                </div>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default MovieCarousel;
