import React, { useState, useEffect } from 'react';
import style from './showcase.module.css';

const Showcase = () => {
    const [videos, setVideos] = useState([]); 
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [currentVideoIndex, setCurrentVideoIndex] = useState(0);

    // Fetch videos from the API
    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/api/movies/');
                const data = await response.json();
                console.log(data); // Check the API response in the console
                setVideos(data || []); // Adjusted to directly set data
            } catch (error) {
                console.error('Error fetching videos:', error);
            }
        };
    
        fetchVideos();
    }, []);

    const handleVideoClick = (videoUrl) => {
        setSelectedVideo(videoUrl);
    };

    const closeModal = () => {
        setSelectedVideo(null);
    };

    // Auto-scroll functionality for videos
    useEffect(() => {
        if (videos.length > 0) {
            const interval = setInterval(() => {
                setCurrentVideoIndex((prevIndex) => (prevIndex + 1) % videos.length);
            }, 3000); // Auto slide every 3 seconds
            return () => clearInterval(interval);
        }
    }, [videos]);

    return (
        <div>
            <div className={style.container}>
                <div className={style.gallerycontainer}>
                    <div className={style.gallerywrapper}>
                        <div
                            className={style.gallerygrid}
                            style={{
                                transform: `translateX(-${currentVideoIndex * 100}%)`,
                                transition: 'transform 1s ease',
                            }}
                        >
                            {videos.map((video, index) => (
                                <div key={index} className={style.galleryitem} onClick={() => handleVideoClick(video.trailer_url)}>
                                    {/* Render the poster image */}
                                    {video.poster_image && (
                                        <img 
                                            src={video.poster_image} 
                                            alt={video.title} 
                                            className={style.posterImage} 
                                        />
                                    )}
                                    <video className={style.galleryvideo} controls>
                                        <source src={video.movie_file} type="video/mp4" />
                                        Your browser does not support the video tag.
                                    </video>
                                </div>
                            ))}
                        </div>
                    </div>

                    {selectedVideo && (
                        <div className={style.modal} onClick={closeModal}>
                            <span className={style.close}>&times;</span>
                            <video className={style.modalcontent} controls>
                                <source src={selectedVideo} type="video/mp4" />
                                Your browser does not support the video tag.
                            </video>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Showcase;
